mapper.select:
	select * from (select reference.*, row_number() over(order by write_date desc) rn from reference where

          title like '%'||#{content}||'%'


         contents like '%'||#{content}||'%'


         contents like '%'||#{content}||'%' or title like '%'||#{content}||'%'

	) where rn between #{startRowNum} and #{endRowNum}
